export const addScrollHandler = (onScrollFunction, element = document) => {
  element.addEventListener("touchmove", onScrollFunction);
  element.addEventListener("scroll", onScrollFunction);
};
export const removeScrollHandler = (onScrollFunction, element = document) => {
  element.removeEventListener("touchmove", onScrollFunction);
  element.removeEventListener("scroll", onScrollFunction);
};
export const getScrollingElement = () => window.document.scrollingElement || window.document.documentElement;